import React,{useState,useEffect} from 'react';
import { useSelector,useDispatch } from 'react-redux';
import {SET_LIBRARY_BOOKS} from '../../store/actions/LibraryActions';
import {getAllBooks} from '../../services/Books';

import Book from '../components/book/BookComponent';
import './ListSection.scss';

const ListSection = props => {

    console.log("ListSection props => ", props);

    const booksListReducer = useSelector(state => (props.from === "Library") ? state.lib : state.lib);
    const books = booksListReducer.books;
    const dispatch = useDispatch();

    console.log("books => ", books);

    const setBooks = (books) => {
        let type = props.from === "Library" ? SET_LIBRARY_BOOKS : SET_LIBRARY_BOOKS;
        dispatch({ type: type, books })
    }

    useEffect(() => {
        getAllBooks().then(books => setBooks(books));
    }, [dispatch])

    return (
        <div style={{ backgroundColor: 'transparent'}} className="pt-3 mt-3 px-3 rounded">
            <div className="row books-list">
                {books.map((book, i) => {
                    return (
                        <div className={"col-6 col-lg-3"} key={book.bookID}>
                            <a href={"#"}>
                                <Book
                                    {...props}
                                    bookID={book.bookID}
                                    title={book.title}
                                    author={book.author}
                                    publisher={book.publisher}
                                    categoryID={book.categoryID}
                                    contributorID={book.contributorID}
                                />
                            </a>
                        </div>
                    );
                })}
            </div>
        </div>
    )
}
export default ListSection
