import React, {useState, useRef} from 'react';
import Filter from '../components/filter/FilterComponent';
import OutSideClick from "../../utils/helper/OutSideClick";

import {
    SET_SEARCH_VALUE
} from '../../store/actions/LibraryActions'

// image
import SearchIcon from '../../assets/images/icons/search-icon.svg'
// import ArrowDown from '../../../../assets/images/icons/arrow-down.png'
// import ArrowDownActive from "../../../../assets/images/icons/arrow-down-active.png"
// import ArrowUPActive from "../../../../assets/images/icons/arrow-up-active.png"
// import SearchIconBig from '../../../../assets/images/icons/search-icon-big.svg'

const ListControlSection = props => {

    console.log("ListControlSection props => ", props);
    const [showSearch, setShowSearch] = useState(false)
    const ref = useRef()

    OutSideClick(ref, () =>{
        if(showSearch){
          setShowSearch(false)
        }
    })

    return (
        <div className="d-flex align-items-center px-3" style={{height: '30px'}}>
                <Filter {...props} />
                {showSearch && (
                    <div ref={ref} className="ml-auto">
                    <input type="text" className="form-control custom-form" placeholder="Search product ..." spellCheck="false" value={valueSearch}
                    onKeyDown={(e) => searchingKey(e)} onKeyUp={(e) => searchingKey(e)} onChange={(e) => setSearch(e)}
                    style={{borderRadius: '50px', borderColor: '#f5a623', height: '36px', paddingRight: '40px'}}/>
                    <div className="position-absolute r-0 text-center smallFont cursorPointer buttonRounded"
                        style={{top: '1px', width: '28px', height: '28px', right: '10px', padding: '5px 0px'}}
                        onClick={() => searching()}><img alt="search" src={SearchIcon}/></div>
                    </div>
                )}
                {!showSearch && (
                    <div className="position-absolute r-0 text-center smallFont cursorPointer buttonRounded"
                    style={{width: '30px', height: '30px', padding: '5px 0px'}}
                    onClick={() => setShowSearch(true)}><img alt="search" src={SearchIcon}/></div>
                )}
            </div>
    )
}
export default ListControlSection
