import {
    ON_HOVER_NAVIGATION,
    ON_RESET_HOVER_NAVIGATION,
    ON_SET_ACTIVE_PAGE,
    ON_TOGGLE_MENU_COLLAPSE
} from '../actions/navigationActions'

// ICONS
import dashboardGreyIcon from '../../assets/images/icons/navigation/home-icon-grey.svg'
import dashboardOrangeIcon from '../../assets/images/icons/navigation/home-icon-orange.svg'
import storeGreyIcon from '../../assets/images/icons/navigation/store-icon-grey.svg'
import storeOrangeIcon from '../../assets/images/icons/navigation/store-icon-orange.svg'
import productGreyIcon from '../../assets/images/icons/navigation/product-icon-grey.svg'
import productOrangeIcon from '../../assets/images/icons/navigation/product-icon-orange.svg'
// END ICONS


const initialState = {
    collapse : false,
    navigationItems : [
        {title : 'Library',icon: '',link:'/',iconGrey : dashboardGreyIcon,iconOrange : dashboardOrangeIcon,hover:false,active:false},
        {title : 'My Books',icon: '',link:'/my-books',iconGrey : storeGreyIcon,iconOrange : storeOrangeIcon,hover:false,active:false},
        {title : 'Log Out',icon: '',link: null ,iconGrey : productGreyIcon,iconOrange : productOrangeIcon,hover:false,active:false},
    ]
}
export default (state = initialState, action) => {
    switch (action.type) {
        case ON_HOVER_NAVIGATION:
            var navigationItems = [...state.navigationItems]
            navigationItems.map(function(x) { 
                x.hover = false;
                return x
            });
            navigationItems[action.index].hover = true
            return {
                ...state,
                navigationItems
            }
        case ON_RESET_HOVER_NAVIGATION : 
            var menu = [...state.navigationItems]
            menu.map(function(x) { 
                x.hover = false;
                return x
            });
            return {
                ...state,
                navigationItems : menu
            }
        case ON_SET_ACTIVE_PAGE : 
            var navigationItems = [...state.navigationItems]
            navigationItems.map(function(x) { 
                x.active = false;
                return x
            });
            const indexFocus = navigationItems.findIndex(res => res.link === action.param)
            navigationItems[indexFocus].active = true
            return {
                ...state,
                navigationItems : navigationItems
            }
        case ON_TOGGLE_MENU_COLLAPSE : 
            var navigation = !state.collapse
            return {
                ...state,
                collapse : navigation
            }
        default:
            return state
    }
}
