import axios from 'axios'
import { API_URL } from '../utils/Variables';

export const attemptLogin = async contributor =>{

    var statusInt = 0
    let token = ''
    let { email, password } = contributor


    await axios.post(API_URL+'auth/login', {
        email,
        password
    })
    .then(function (response) {
        console.log("attemptLogin Response => ", response);
        if(response.data.status){
            statusInt = (response.data.data.token ? 1 : 2)
            token = (response.data.data.token ? response.data.data.token : '')
        }else{
            statusInt = 2
            token = null
        }

    })
    .catch(function (error) {
        console.log("attemptLogin Error => ", error)
        statusInt = 3
    });

    return {
        statusInt,
        token
    }
}

