import React, {useEffect, useState} from 'react';
import './BookComponent.scss';
import logoCwi  from '../../../assets/images/general/logo-cwi.png';
import {CATEGORIES} from '../../../utils/Variables';
import {getContributorDetail} from '../../../services/Contributors';

const BookComponent = props => {

    const [contributorName, setContributorName] =useState(false)

    console.log("BookComponent props => ", props);
    const {title, author, publisher, categoryID, contributorID} = props;

    useEffect(() => {
        getContributorDetail(props.contributorID).then((contributor) => {
            setContributorName
        })
    }, [])

    let categoryIndex = -1;
    for (let i=0; i<CATEGORIES.length; i++) {
        if (categoryID == CATEGORIES[i].categoryID) {
            categoryIndex = i;
            break;
        }
    }

    return (
        <div className={"bgWhite rounded book-item"} style={{ boxShadow: '0px 0px 20px rgba(0,0,0,0.2)' }}>
            <div className="image-content position-relative">
                <img src={logoCwi} className="w-100 rounded-top" alt={props.title} />
                {/* <span className="nunitoBold smallFont white-color obj-el">Promo</span> */}
            </div>
            <div className="p-3">
                <div className="">{title} <span className="" style={{fontSize:"10px"}}>({CATEGORIES[categoryIndex].categoryName})</span></div>
                <div className="">{author}</div>
                <div className="">{publisher}</div>
                <div className="">{contributorID}</div>
                {/* <div className={isBrowser ? "nunitoRegular smallFont mb-1 original-price" : "nunitoRegular smallFont mb-0 original-price"} style={{ letterSpacing: "-0.36px" }}><span className="obj-el">{p.dp}</span></div> */}
            </div>
        </div>
    )
}
export default BookComponent
