import React , {useState,useEffect} from 'react'
import { NavLink } from 'react-router-dom'
import {attemptLogin,setLocalStorageAuth} from '../../../../services/Auth'

// Images
import backgroundImage from '../../../../assets/images/login/background.png'
import logoCwi from '../../../../assets/images/general/logo-cwi.png'


const LoginContainer = props => {

    const [isLoginSubmitted, setIsLoginSubmitted] = useState(false)
    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')
    const [loginFailed, setLoginFailed] = useState(false)

    useEffect(() => {
        if(localStorage.getItem('authToken') !== null) props.history.push('/')
    })

    const loginHandler = e => {
        e.preventDefault()

        setIsLoginSubmitted(true)
        setLoginFailed(false)

        const credentials = {
            email: email,
            password: password
        }

        attemptLogin(credentials).then(function(result) {
            if(result.statusInt === 1){
                setLocalStorageAuth(result.token)
                props.history.push('/')
            }else{
                setIsLoginSubmitted(false)
                setLoginFailed(true)
            }
        })
    };
    const registerHandler = e => {
        console.log("Registering");
    }

    return(
        <div 
        className="position-absolute backgroundImageLogin backgroundImageCenter leftNull topNull fluidHeight fluidWidth" 
        style={{backgroundImage:`url(${backgroundImage})`,backgroundSize:'cover',backgroundPosition:'center center'}}>
            <div className="d-flex justify-content-center align-items-center fluidHeight fluidWidth">


                <div style={{width:524}}>
                    <div className="card" style={{borderRadius:16,height:560}}>
                        <div className="card-body text-center" style={{paddingLeft:109,paddingRight:109,paddingTop:95,paddingBottom:95}}>
                            <form className="d-flex flex-column justify-content-between h-100" onSubmit={loginHandler}>
                                <div>
                                    <img alt="Cigarworld Indonesia" src={logoCwi} width="114"/>
                                </div>
                                <div>

                                {loginFailed ?
                                <div className="alert alert-danger p-1">
                                    <span className="smallFont">Email or password invalid</span>
                                </div> : null }
                                    <div className="form-group">
                                        <input onChange={(e)=>setEmail(e.target.value)} value={email} type="text" required className="form-control greySmooth normalFont" style={{height:45,opacity:0.5}} placeholder="Email Address"/>
                                    </div>
                                    <div className="form-group mb-0">
                                        <input onChange={(e)=>setPassword(e.target.value)} value={password} type="password" required className="form-control greySmooth normalFont" style={{height:45,opacity:0.5}} placeholder="Password"/>
                                    </div>
                                    {/* <div className="text-center mt-1">
                                        <Link to="#" className="greyDefault smallFont">No account yet? Register here</Link>
                                    </div> */}
                                    <div style={{marginTop: '45px', letterSpacing: '0.08px'}} className="text-center">
                                        <h5 className="mb-0 dusk smallFont">New to Bigas ? <span style={{color:'#f5a623', cursor:"pointer"}} onClick={registerHandler} className="fontWeightSemiBold">Register here</span></h5>
                                    </div>
                                </div>
                                <div>
                                    <button type="submit" disabled={isLoginSubmitted} className="buttonDefault fluidWidth rounded-pill">Log In</button>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}
export default LoginContainer
