import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import Notifications, { notify } from 'react-notify-toast'
import './BookOperationsContainer.scss';
import logoCwi from '../../../../../assets/images/general/logo-cwi.png';

const BookOperationsContainer = props => {

    console.log("BookOperationsContainer props => ", props);

    const id = props.match.params.id ? props.match.params.id : null;
    const mode = (id == null) ? "add" : "edit";

    const mybook = useSelector(state => state.mybook)
    const dispatch = useDispatch()

    // useEffect(() => {
    //     if (mode === "edit") {
    //         console.log("IN EDIT MODE");
    //         // const setStoreDetail = (data) => dispatch({type: SET_STORE_DETAIL, data})
    //         // getStoreData(id).then(res => {setStoreDetail(res)})
    //     }
    // }, [dispatch]);

    // const handleSubmitEditStore = (e) =>{
    //     e.preventDefault()

    //     const data = {
    //         id: parseInt(id),
    //         storeName: store.storeData.storeName,
    //         contactNumber: store.storeData.phoneNumber,
    //         cityId: 1,
    //         storeAddress: store.storeData.storeAddess,
    //         storeEmail: store.storeData.email,
    //         status: store.storeData.isActive
    //     }
    //     editStore(data).then(res => {
    //         if(res.status === true) {
    //             notify.show(res.message, 'success', 1000)
    //         }
    //         else{
    //             notify.show("Terjadi Kesalahan pada Sistem", 'error', 1000)
    //         }
    //         // dispatch({type: RESET_brand_FORM})
    //         setTimeout(() => props.history.push('/store/detail/' + id), 1000)
    //     })
    // }

    return (
        <Suspense fallback={<div className="lds-ring mt-2"><div></div><div></div><div></div><div></div></div>}>
            <div className="book-operations-container d-flex flex-column">
                <div className="image-section d-flex flex-column align-items-center">
                    {/* <div className="photo text-center d-flex align-items-center justify-content-center"> */}
                        <img src={logoCwi} alt="logoCwi" height="20px" />
                    {/* </div> */}
                </div>
                <div className="formSection flex-grow-1 d-flex flex-column">
                    <Notifications />
                    <div className="w-100 content-form">
                        <form autoComplete="off">
                            <h4 className="biggerFont colorDefault titleSection d-inline-block">Store Description</h4>
                            <div className="row">
                                <div className="col-xl-4 col-lg-6">
                                    <h5 className="smallFont greyMedium text-uppercase">USERNAME</h5>
                                    <div className="normalFont greyDefault ">{store.storeData.userName}</div>
                                </div>
                                <div className="col-xl-4 col-lg-6">
                                    <h5 className="smallFont greyMedium text-uppercase">STORE NAME</h5>
                                    <input value={store.storeData.storeName} name="storeName" type="text" onChange={(e) => dispatch({ type: EDIT_STORE_DETAIL, e })} className="form-control custom-form" placeholder="Enter Store Name" />
                                </div>
                                <div className="col-xl-4 col-lg-6">
                                    <h5 className="smallFont greyMedium text-uppercase">CITY</h5>
                                    <input value={store.storeData.cityName} name="cityName" type="text" onChange={(e) => dispatch({ type: EDIT_STORE_DETAIL, e })} className="form-control custom-form" placeholder="Enter Store City Name" />
                                </div>
                                <div className="col-xl-4 col-lg-6">
                                    <h5 className="smallFont greyMedium text-uppercase">ADDRESS</h5>
                                    <input value={store.storeData.storeAddess} name="storeAddess" type="text" onChange={(e) => dispatch({ type: EDIT_STORE_DETAIL, e })} className="form-control custom-form" placeholder="Enter Store Address" />
                                </div>
                                <div className="col-xl-4 col-lg-6">
                                    <h5 className="smallFont greyMedium text-uppercase">PHONE NUMBER</h5>
                                    <input value={store.storeData.phoneNumber} name="phoneNumber" type="number" onChange={(e) => dispatch({ type: EDIT_STORE_DETAIL, e })}
                                        className="form-control custom-form" placeholder="Enter Store Phone" />
                                </div>
                                <div className="col-xl-4 col-lg-6">
                                    <h5 className="smallFont greyMedium text-uppercase">EMAIL</h5>
                                    <input value={store.storeData.email} name="email" type="email" onChange={(e) => dispatch({ type: EDIT_STORE_DETAIL, e })} className="form-control custom-form" placeholder="Enter Store Email" />
                                </div>
                                <div className="col-xl-4 col-lg-6">
                                    <h5 className="smallFont greyMedium text-uppercase">JOIN DATE</h5>
                                    <div className="normalFont greyDefault " style={{ opacity: 0.5 }}>{moment(new Date(store.storeData.joinDate)).format('DD / MM / YYYY')}</div>
                                </div>
                                <div className="col-xl-4 col-lg-6">
                                    <h5 className="smallFont greyMedium text-uppercase">LAST ACTIVE</h5>
                                    <div className="row normalFont greyDefault" style={{ opacity: 0.5 }}>
                                        <div className="col-lg-6">{moment(new Date(store.storeData.lastActive)).format('DD / MM / YYYY')}</div>
                                        <div className="col-lg-1 d-flex flex-column align-items-center">
                                            <img className="my-auto" src={clock} alt="Clock Icon" height="15px" />
                                        </div>
                                        <div className="col-lg-4">{moment(new Date(store.storeData.lastActive)).format('h:mm A')}</div>
                                    </div>
                                </div>
                            </div>
                        </form>
                    </div>
                    <div className="mt-auto text-right">
                        <button disabled={
                            ((
                                store.storeData.storeName === '' ||
                                store.storeData.cityName === '' ||
                                store.storeData.storeAddess === '' ||
                                store.storeData.phoneNumber === '' ||
                                store.storeData.email === ''
                            ) ? true : false)
                        } onClick={(e) => handleSubmitEditStore(e)} className="buttonRounded d-inline-block px-4">Save Changes</button>
                    </div>
                </div>
            </div>
        </Suspense>
    )
}
export default BookOperationsContainer
