import {
    SET_ACTIVE_CATEGORIES,
    SET_LIBRARY_BOOKS
} from '../actions/LibraryActions'

const DUMMY_BOOKS = [
    {
        "bookID": 1,
        "contributorID": 1,
        "title": "Menteng is Heaven",
        "author": "Albert",
        "publisher": "Penguin",
        "categoryID": 0
    },
    {
        "bookID": 3,
        "contributorID": 1,
        "title": "Around the World in 0.02 Seconds",
        "author": "Flash",
        "publisher": "DC",
        "categoryID": 6
    },
    {
        "bookID": 4,
        "contributorID": 2,
        "title": "Guide to Birdwatching in Menteng",
        "author": "Tara",
        "publisher": "BBS",
        "categoryID": 6
    },
    {
        "bookID": 5,
        "contributorID": 4,
        "title": "My Diary",
        "author": "Anies Baswedan",
        "publisher": "Undisclosed",
        "categoryID": 5
    },
    {
        "bookID": 6,
        "contributorID": 2,
        "title": "Best Friend",
        "author": "Young Thug",
        "publisher": "ABC",
        "categoryID": 5
    },
    {
        "bookID": 7,
        "contributorID": 2,
        "title": "The Plague",
        "author": "Winnie TP",
        "publisher": "CCP",
        "categoryID": 1
    }
]

const initialState = {
    activeCategories: [],
    books: []
}
export default (state = initialState, action) => {
    switch (action.type) {
        case SET_ACTIVE_CATEGORIES:

            var activeCategories = [...state.activeCategories]
            var categoryFoundIndex = -1;

            for (let i = 0; i < activeCategories.length; i++) {
                if (activeCategories[i] == action.categoryID) {
                    categoryFoundIndex = i;
                    break;
                }
            }

            (categoryFoundIndex == -1) ?
                activeCategories.push(action.categoryID) :
                activeCategories.splice(categoryFoundIndex, 1);

            return {
                ...state,
                activeCategories
            }
        case SET_LIBRARY_BOOKS:

            // var libraryBooks = action.libraryBooks ...later replace with actual data from api
            var books = action.books;

            return {
                ...state,
                books
            }
        default:
            return state
    }
}
