import React, { useEffect } from 'react'
import { Link } from 'react-router-dom'


// Redux
import { useDispatch, useSelector } from 'react-redux'
import {
    ON_HOVER_NAVIGATION,
    ON_RESET_HOVER_NAVIGATION,
    ON_SET_ACTIVE_PAGE,
    ON_TOGGLE_MENU_COLLAPSE
} from '../../store/actions/NavigationActions';

// Custom Style
import './NavigationLeft.scss'
// End Custom Style

// Icons
import minimizeIcon from '../../assets/images/icons/minimize.png'
import minimizeIconMobile from '../../assets/images/icons/minimize-mobile.png'
import bigasLogo from '../../assets/images/general/logo-cwi.png'

const NavigationLeft = () => {
    const dispatch = useDispatch()
    const navigation = useSelector(state => state.nav)

    let urlParam = window.location.pathname.split('/')
    const param = '/' + urlParam[1]

    console.log(param);

    useEffect(() => {
            dispatch({ type: ON_SET_ACTIVE_PAGE, param })
    }, [dispatch, param])

    const handleHoverNavigation = (index) => {
        dispatch({ type: ON_HOVER_NAVIGATION, index })
    }
    const handleResetHoverNavigation = () => {
        dispatch({ type: ON_RESET_HOVER_NAVIGATION })
    }
    const handleCollapseMenu = () => {
        dispatch({ type: ON_TOGGLE_MENU_COLLAPSE })
    }

    return (
        <div className="py-4 bgWhite">
            <ul>
                <li>
                    <div className="mb-5" style={{ cursor: 'pointer', textAlign: (navigation.collapse ? 'center' : 'left') }}>

                        <img height="14" onClick={() => handleCollapseMenu()} src={(navigation.collapse ? minimizeIconMobile : minimizeIcon)} alt="minimize" className="mx-4 cursor-pointer" />
                        {!navigation.collapse ?
                            <img width="52" src={bigasLogo} alt="BIGAS" />
                            : null}
                    </div>
                </li>
                {navigation.navigationItems.map((data, i) => {
                    return (
                        <li key={i} className="position-relative">
                            <Link
                                onMouseOver={() => handleHoverNavigation(i)}
                                onMouseLeave={() => handleResetHoverNavigation()}
                                to={data.link}
                                className={['py-3 d-block greyDefault itemLink position-relative', (data.active ? 'activeLink' : ''), (navigation.collapse ? 'text-center' : '')].join(' ')}>
                                <span className="pinActive d-inline-block position-absolute"></span>
                                <img className="mx-4" src={(data.hover || data.active ? data.iconOrange : data.iconGrey)} alt={data.title} />

                                {navigation.collapse ? '' : data.title}
                                {!navigation.collapse && data.title === 'Order' && navigation.orderNotif !== 0 && (
                                    <div className="position-absolute r-0 text-center smallFont"
                                        style={{
                                            top: '17px', border: 'none', borderRadius: '50px', width: '25px', height: '25px',
                                            padding: '4px 0px', color: 'white', backgroundColor: '#f5a623'
                                        }}>{navigation.orderNotif}</div>
                                )}
                            </Link>
                        </li>
                    )
                })}
            </ul>
        </div>
    )
}


export default NavigationLeft
