import axios from "axios"
import { API_URL } from "../../utils/Variable"

export const getAllBooks = async () => {

    var data

    await axios
        .get(API_URL + "books/all")
        .then(function (response) {
            console.log("getAllBooks Response => ", response);
            //   data = response.data
        })
        .catch(function (error) {
            console.log("getAllBooks error => ", error);
            //   data = error
        });

    return data;
}

export const getBookDetail = async bookId => {

    var data

    await axios
        .get(API_URL + "books/" + bookId)
        .then(function (response) {
            console.log("getBookDetail Response => ", response);
            //   data = response.data
        })
        .catch(function (error) {
            console.log("getBookDetail error => ", error);
            //   data = error
        });

    return data;
}

export const editBookDetails = async (bookData) => {

    let data = []
    let statusInt = 0
    let { bookId } = bookData;

    const headers = {
        'Content-Type': 'application/json',
        // 'Authorization' : storage.token
    }

    await axios.put(API_URL + '/books/' + bookId + '/edit', bookData, {headers: headers})
        .then(function (response) {
            console.log("editBookDetails Response => ", response);
            // if (response.data.code === 200) {
            //     statusInt = 1
            // } else {
            //     statusInt = 2
            // }
            // data = response.data.data
        })
        .catch(function (error) {
            console.log("editBookDetails error => ", error);
            // statusInt = 3
        })
    return { data, statusInt }
}

export const createBook = async (contributorId, bookData) => {

    // const storage = JSON.parse(localStorage.getItem('authToken'));
    const data = {contributorId, bookData};

    let result = []

    const headers = {
        'Content-Type': 'application/json',
        // 'Authorization' : storage.token
    }

    await axios.post(API_URL+'book/create', data, {headers : headers})
    .then(function (response) {
        console.log(response)
        // result = response.data
    })
    .catch(function (error) {
        console.log(error)
    });

    return result
}

export const deleteBook = async (bookId) => {

    // const storage = JSON.parse(localStorage.getItem('authToken'));

    let result = []

    const headers = {
        'Content-Type': 'application/json',
        // 'Authorization' : storage.token
    }

    await axios.delete(API_URL+'books/' + bookId + '/delete')
    .then(function (response) {
        console.log(response)
        // result = response.data
    })
    .catch(function (error) {
        console.log(error)
    });

    return result
}
