import React from 'react';
import { NavLink } from 'react-router-dom'
import './BookComponent.scss';
import logoCwi from '../../../assets/images/general/logo-cwi.png';
import editButton from '../../../assets/images/icons/edit-icon-orange.svg';
import deleteButton from '../../../assets/images/icons/remove-icon.svg';
import addButton from '../../../assets/images/icons/upload.svg';
import { CATEGORIES } from '../../../utils/Variables';

const BookComponent = props => {

    console.log("BookComponent props => ", props);
    const { title, author, publisher, categoryID } = props || {};

    let categoryIndex = -1;
    for (let i = 0; i < CATEGORIES.length; i++) {
        if (categoryID == CATEGORIES[i].categoryID) {
            categoryIndex = i;
            break;
        }
    }

    const deleteBookHandler = () => {
        console.log("Deleting Book...");
    }

    return (
        <div className={"bgWhite rounded book-component"} style={{ boxShadow: '0px 0px 20px rgba(0,0,0,0.2)' }}>
            {props.isAddBook ?
                <div className="add-book">
                    <img src={addButton} className="rounded-top" alt="add" />
                    <h3 style={{ marginTop: "10px" }}>Add New Book</h3>
                </div>
                :
                <div>
                    <div className="book-image position-relative">
                        <img src={logoCwi} className="w-100 rounded-top" alt={props.title} />
                        {props.from === "MyBooks" &&
                            <span className="d-flex align-items-center justify-content-center" style={{ right: "15px" }}>
                                <NavLink to={'/my-books/edit/' + props.bookID}>
                                    <img src={editButton} alt="edit" />
                                </NavLink>
                            </span>
                        }
                        {props.from === "MyBooks" &&
                            <span onClick={deleteBookHandler} className="d-flex align-items-center justify-content-center" style={{ right: "50px" }}>
                                <img src={deleteButton} alt="delete" />
                            </span>
                        }
                    </div>
                    <div className="p-3">
                        <div className="">{title} <span className="" style={{ fontSize: "10px" }}>({CATEGORIES[categoryIndex].categoryName})</span></div>
                        <div className="">{author}</div>
                        <div className="">{publisher}</div>
                    </div>
                </div>
            }
        </div>
    )
}
export default BookComponent
